import React, { Component } from 'react'
import NewsItem from './NewsItem'
 

export class News extends Component {
  constructor(){
    super();
    this.state = {
      articles: [],
      loading: false
      
    }
  }
  async componentDidMount(){
    console.log("cdm")
    let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=697bea5637814035a04c8fb792e8302b";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles: parsedData.articles})
  }
  render() {
    console.log("render")
    return (
      <div className='container my-3 justify-content-center'>
      <h1 className='mx-3'><strong>NSWNEWS - Top Headline in India</strong></h1>
      <div className="row">
      {this.state.articles.map((element)=>{
        return <div className="col-md-6" key={element.url} > 
        <NewsItem title={element.title?element.title.slice(0, 45):" "} description={element.description?element.description.slice(0, 90):""} imageurl={element.urlToImage} newsurl={element.url}/>
        </div> 
      })}
      
        </div>
      </div>
    )
  }
}

export default News
