import React, { Component } from 'react'
import NewsItem from './NewsItem'
 

export class News extends Component {
  constructor(){
    super();
    this.state = {
      articles: [],
      loading: false
      
    }
  }
  async componentDidMount(){
    console.log("cdm")
    let url = "https://newsapi.org/v2/top-headlines?country=in&category=business&apiKey=697bea5637814035a04c8fb792e8302b";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(parsedData);
    this.setState({articles: parsedData.articles})
  }

 handlePrevClick = ()=>{

 }
 handleNextClick = ()=>{

 }


  render() {
    console.log("render")
    return (
      <div className='container justify-content-center'>
      <h1 className='mx-3'><strong>NSWNEWS - Top Headline in India</strong></h1>
      <div className="row">
      {this.state.articles.map((element)=>{
        return <div className="col-md-3" key={element.url} > 
        <NewsItem title={element.title?element.title.slice(0, 45):" "} description={element.description?element.description.slice(0, 90):""} imageurl={element.urlToImage} newsurl={element.url}/>
        </div> 
      })}
      
        </div>
        <div className='container d-flex justify-content-between my-3'>
        <button type="button" className="btn btn-danger" onClick={this.handlePrevClick}> &larr; Previous</button>
        <button type="button" className="btn btn-danger" onClick={this.handleNextClick}>next &rarr;</button>
        </div>
      </div>
    )
  }
}

export default News
