import React, { Component } from 'react'
import Footer from './Footer'

export class Latestnews extends Component {

   constructor(){
    super();
    this.state = {
        articles: this.articles,
        loading: false
    }
   }

   async componentDidMount(){
    let url ="https://newsapi.org/v2/top-headlines?sources=techcrunch&apiKey=697bea5637814035a04c8fb792e8302b";
    let data = await fetch(url);
    let parsedData = await data.json()
    console.log(data);


   }

  render() {
    return (
      <div>
      {this.state.articles.map((element)=>
        <Footer  title={element.title?element.title.slice(0, 45):" "} description={element.description?element.description.slice(0, 90):""} imageurl={element.urlToImage} newsurl={element.url} author={element.author} date={element.publishedAt} source={element.source.name}/>
      )}
      </div>
    )
  }
}

export default Latestnews
