import React, { Component } from 'react'
import Footer from './Footer'

export class Latestnews extends Component {


    static defaultProps ={
        country: "in",
        pageSize: 12,
        category: "general",
      }
      
       
    
      constructor(props){
        super(props);
        this.state = {
          articles: [],
          loading: false,
          page: 1
          
        }
       
      }
      async componentDidMount(){
        console.log("cdm")
        let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=697bea5637814035a04c8fb792e8302b&page=1&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parsedData = await data.json()
        console.log(parsedData);
        this.setState({articles: parsedData.articles, totalResults: parsedData.totalResults, loading: false})
      }
    
       
    
       
    
    
      render() {
        console.log("render")
        return (
           
          
          
          <div className="row">
          {!this.state.loading && this.state.articles.map((element)=>{
            return <div className='kilimanjaro_part col-md-1' key={element.url} > 
            <Footer  title={element.title?element.title.slice(0, 45):" "} description={element.description?element.description.slice(0, 90):""} imageurl={element.urlToImage} newsurl={element.url}/>
            </div> 
            
          })}
        
          </div>
        )
      }
    }
export default Latestnews
